import React from "react";
import "./TestimonialsSection.scss";
import { useState } from "react";

function TestimonialsSection() {
  // hooks
  const [currentSlide, setCurrentSlide] = useState(0);

  // data object
  const testimonials = [
    {
      id: "1",
      name: "Jane Smith",
      testimonial:
        "lorem25asdlkkhalskdeisdfgsdfhgsfhdfgdshsfghfdgnsfgndfgnuhfalsdjkfhna;eiwoeihfsdkjfnh.l",
      image: "assets/testimonial.png",
    },
    {
      id: "2",
      name: "Alana Keeton",
      testimonial: "lorem25",
    },
  ];

  // map through data in object using hooks, on click
  const handleClick = (direction) => {
    direction === "left"
      ? setCurrentSlide(currentSlide > 0 ? currentSlide - 1 : 2)
      : setCurrentSlide(
          currentSlide < testimonials.length - 1 ? currentSlide + 1 : 0
        );
  };

  return (
    <section className="testimonials-section">
      <div className="heading">
        <h4 className="section-title">TESTIMONIALS</h4>
        <h2 className="headline">See What My Satisfied Clients Have To Say</h2>
        <h3 className="subtitle">
          I value my clientsâ€™ feedback and I am proud to showcase their kind
          words.
        </h3>
      </div>
      <div className="border"></div>
      <div
        className="slider"
        style={{ transform: `translateX(-${currentSlide * 100}vw)` }}
      >
        {testimonials.map((i) => (
          <div className="testimonial-container">
            <div className="testimonial">
              <div className="left">
                {/* <div className="leftContainer"> */}
                <h1>{i.name}</h1>
                <h3>{i.testimonial}</h3>
                <div className="arrows">
                  <img
                    src="assets/left-arrow.png"
                    className="arrow left"
                    alt=""
                    onClick={() => handleClick("left")}
                  />
                  <h1>{i.id}</h1>
                  <img
                    src="assets/right-arrow.png"
                    className="arrow right"
                    alt=""
                    onClick={() => handleClick("right")}
                  />
                  {/* </div> */}
                </div>
              </div>
              <div className="rightContainer">
                <img src={i.image} alt="" />
              </div>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}

export default TestimonialsSection;
