  // let currentQuote = document.getElementById("positive-quote").textContent;
  // let currentAuthor = author;
  // console.log(currentQuote);

  // const displayQuotes = () => {
  //   if (
  //     currentQuote === positiveQuotes[0].quoteText &&
  //     currentAuthor === positiveQuotes[0].author
