import { useState } from "react";
import "./PositiveQuotes.scss";

const allQuotes = [
  {
    quote:
      "All our dreams can come true, if we have the courage to pursue them.",
    author: "Walt Disney",
  },
  {
    quote:
      "In order to carry a positive action we must develop here a positive vision.",
    author: "Dalai Lama",
  },
  {
    quote:
      "Extraordinary things are always hiding in places people never think to look.",
    author: "Jodi Picoul",
  },
];

const PositiveQuotes = () => {
  const [quote, setQuote] = useState(allQuotes[0].quote);
  const [author, setAuthor] = useState(allQuotes[0].author);
  const [opacity, setOpacity] = useState(1);

  let currQuote = 0;
  const maxQuotes = allQuotes.length - 1;

  const changeQuote = (arr) => {
    allQuotes.forEach((q, i) => {
      if (opacity === 0) {
        if (q[i] === maxQuotes) setQuote((q[i] = 0));
        else setQuote(q[i]++);
      } else {
        setQuote(q[i]);
      }
    });
  };

  setInterval(() => {
    setOpacity(0);
  }, 3000);

  setInterval(() => {
    setOpacity(1);
    changeQuote(allQuotes.entries());
  }, 6000);

  return (
    <div className="positive-quotes-container">
      <div className="quote-container" style={{ opacity: opacity }}>
        <blockquote className="positive-quote">{quote}</blockquote>
        <p className="quote-author">- {author}</p>
      </div>
      {/* <div className="quote-container" style={{ opacity: 0 }}>
        <blockquote className="positive-quote">{allQuotes[1].quote}</blockquote>
        <p className="quote-author">- {allQuotes[1].author}</p>
      </div>
      <div className="quote-container" style={{ opacity: 0 }}>
        <blockquote className="positive-quote">{allQuotes[2].quote}</blockquote>
        <p className="quote-author">- {allQuotes[2].author}</p>
      </div> */}
    </div>
  );
};

export default PositiveQuotes;
