// use transform: translateX to put all quotes next to each other
// set opacity after 10000 seconds to 0
// shift quotes over 100% more or less
// set opacity back to 10

import "./PositiveQuotes.scss";

const PositiveQuotes = () => {
  const quotes = document.querySelectorAll(".quote-container");
  quotes.forEach((quote, i) => {
    quote.style.transform = `translateX(${100 * (i - quote)}%)`;
  });

  return (
    <div className="positive-quotes-container">
      <div
        className="quote-container"
        style={{ transform: "translateX(-100%)" }}
      >
        <blockquote className="positive-quote">
          "All our dreams can come true, if we have the courage to pursue them."
        </blockquote>
        <p className="quote-author">- Walt Disney</p>
      </div>
      <div className="quote-container" style={{ transform: "translateX(0%)" }}>
        <blockquote className="positive-quote">
          “In order to carry a positive action we must develop here a positive
          vision.”
        </blockquote>
        <p className="quote-author">- Dalai Lama</p>
      </div>
      <div
        className="quote-container"
        style={{ transform: "translateX(100%)" }}
      >
        <blockquote className="positive-quote">
          “Extraordinary things are always hiding in places people never think
          to look.”
        </blockquote>
        <p className="quote-author">- Jodi Picoul</p>
      </div>
    </div>
  );
};

export default PositiveQuotes;
